(function() {
  var pagan = function() {
    var args = [].slice.call(arguments),
        length = args.length, i = 0;
    this.length = length;
    
    for ( ; i < length; i++) {
      this[i] = args[i];
    }
    
    return this;
  }

  pagan.prototype = new Array();
 
  if(typeof module !== 'undefined' && module.exports) {
    module.exports = pagan;
  }

  pagan.prototype.setPage = function(p) { this.page = p; }
 
  pagan.prototype.setIncrement = function(i) {
    this.increment = i;
  }

  pagan.prototype.values = function() {
    var that = this,
        count = that.increment,
        end = that.page * that.increment,
        start = end - that.increment,
        values = [];

    for(x = start; x < end; x++) {
      if(that[x] !== undefined) {
        values.push(that[x]);
      }
      count --;
    }
    if(count <= 0) {
      return values;
    }
  }
}());

